// App.js
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Button } from 'reactstrap';
import React from 'react';

class App extends React.Component {
  constructor(){
    super();
    this.state = {
      saludo: "Esta aplicacion saluda en varios idiomas"
    };
    this.mensajeIngles = this.mensajeIngles.bind(this);
    this.mensajeSueco = this.mensajeSueco.bind(this);
    this.mensajeAleman = this.mensajeAleman.bind(this);
    this.mensajeEspanol = this.mensajeEspanol.bind(this);
  }
  mensajeIngles(){this.setState({saludo: "Hello"});}
  mensajeSueco(){this.setState({saludo: "Hej"});}
  mensajeAleman(){this.setState({saludo: "Hallo"});}
  mensajeEspanol(){this.setState({saludo: "Hola"});}
  render() {
    return (
      <div className="App">
        <h3>{this.state.saludo}</h3>
        <Button color="warning">Inglés</Button>
        <Button color="primary">Alemán</Button>
        <Button color="danger">Sueco</Button>
        <Button color="info">Español</Button>
      </div>
    );
  }
}

export default App; // <-- aquí exportas el componente
