// App.js
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Button } from 'reactstrap';
import React from 'react';

class App extends React.Component {
  constructor(){
    super();
    this.state = {
      saludo: "Esta aplicacion saluda en varios idiomas"
    };
  }
  mensajeIngles = () => { this.setState({ saludo: "Hello" }); }
  mensajeSueco = () => { this.setState({ saludo: "Hej" }); }
  mensajeAleman = () => { this.setState({ saludo: "Hallo" }); }
  mensajeEspanol = () => { this.setState({ saludo: "Hola" }); }
  render() {
    return (
      <div className="App">
        <h3>{this.state.saludo}</h3>
        <Button color="warning" onClick={this.mensajeIngles}>Inglés</Button>
        <Button color="primary" onClick={this.mensajeAleman}>Alemán</Button>
        <Button color="danger" onClick={this.mensajeSueco}>Sueco</Button>
        <Button color="info" onClick={this.mensajeEspanol}>Español</Button>
      </div>
    );
  }
}

export default App;
